/*jslint browser: true, devel: true, indent: 4, maxerr: 50, sub: true */
/*global jQuery, tb_show, tb_remove */

/**
 * Custom jQuery for Custom Metaboxes and Fields
 */
jQuery(document).ready( function($) {
	'use strict';

	var formfield;
	// Uploading files
	var file_frames = {};
	var iterator = 0;

	/**
	 * Initialize timepicker (this will be moved inline in a future release)
	 */
	$('.cpmb_timepicker').each( function() {
		$('#' + jQuery(this).attr('id')).timePicker({
			startTime: "00:00",
			endTime: "23:59",
			show24Hours: false,
			separator: ':',
			step: 30
		});
	});

	/**
	 * Initialize jQuery UI datepicker (this will be moved inline in a future release)
	 */
	$('.cpmb_datepicker').each( function() {
		$('#' + jQuery(this).attr('id')).datepicker();
		// $('#' + jQuery(this).attr('id')).datepicker({ dateFormat: 'yy-mm-dd' });
		// For more options see http://jqueryui.com/demos/datepicker/#option-dateFormat
	});
	// Wrap date picker in class to narrow the scope of jQuery UI CSS and prevent conflicts
	$("#ui-datepicker-div").wrap('<div class="cpmb_element" />');

	/**
	 * Initialize color picker
	 */
	if (typeof jQuery.wp === 'object' && typeof jQuery.wp.wpColorPicker === 'function') {
		$('input:text.cpmb_colorpicker').wpColorPicker();
	} else {
		$('input:text.cpmb_colorpicker').each( function(i) {
			$(this).after('<div id="picker-' + i + '" style="z-index: 1000; background: #EEE; border: 1px solid #CCC; position: absolute; display: block;"></div>');
			$('#picker-' + i).hide().farbtastic($(this));
		})
		.focus( function() {
			$(this).next().show();
		})
		.blur( function() {
			$(this).next().hide();
		});
	}

	/**
	 * File and image upload handling
	 */


	$('.cpmb_metabox')
	.on( 'change', '.cpmb_upload_file', function() {
		formfield = $(this).attr('name');
		$('#' + formfield + '_id').val("");
	})
	.on( 'click', '.cpmb_upload_button', function(event) {

		event.preventDefault();

		var $self = $(this);
		formfield = $self.prev('input').attr('id');
		
		
		var $formfield = $('#'+formfield);
		var uploadStatus = true;
		var attachment = true;
		var isList = $self.hasClass( 'cpmb_upload_list' );


		// If this field's media frame already exists, reopen it.
		if ( formfield in file_frames ) {
			file_frames[formfield].open();
			return;
		}

		// Create the media frame.
		file_frames[formfield] = wp.media.frames.file_frame = wp.media({
			title: $('label[for=' + formfield + ']').text(),
			button: {
				text: window.cpmb_l10.upload_file
			},
			multiple: isList ? true : false
		});

		// When an file is selected, run a callback.
		file_frames[formfield].on( 'select', function() {


			if ( isList ) {

				// Get all of our selected files
				attachment = file_frames[formfield].state().get('selection').toJSON();

				$formfield.val(attachment.url);
				$('#'+ formfield +'_id').val(attachment.id);

				// Setup our fileGroup array
				var fileGroup = [];

				// Loop through each attachment
				$( attachment ).each( function() {
					if ( this.type && this.type === 'image' ) {
						// image preview
						uploadStatus = '<li class="img_status">'+
							'<img width="50" height="50" src="' + this.url + '" class="attachment-50x50" alt="'+ this.filename +'">'+
							'<p><a href="#" class="cpmb_remove_file_button" rel="'+ formfield +'['+ this.id +']">'+ cpmb_l10.remove_image +'</a></p>'+
							'<input type="hidden" id="filelist-'+ this.id +'" name="'+ formfield +'['+ this.id +']" value="' + this.url + '">'+
						'</li>';

					} else {
						// Standard generic output if it's not an image.
						uploadStatus = '<li>'+ cpmb_l10.file +' <strong>'+ this.filename +'</strong>&nbsp;&nbsp;&nbsp; (<a href="' + this.url + '" target="_blank" rel="external">'+ cpmb_l10.download +'</a> / <a href="#" class="cpmb_remove_file_button" rel="'+ formfield +'['+ this.id +']">'+ cpmb_l10.remove_file +'</a>)'+
							'<input type="hidden" id="filelist-'+ this.id +'" name="'+ formfield +'['+ this.id +']" value="' + this.url + '">'+
						'</li>';

					}

					// Add our file to our fileGroup array
					fileGroup.push( uploadStatus );
				});

				// Append each item from our fileGroup array to .cpmb_media_status
				$( fileGroup ).each( function() {
					$formfield.siblings('.cpmb_media_status').slideDown().append(this);
				});

			} else {

				// Only get one file from the uploader
				attachment = file_frames[formfield].state().get('selection').first().toJSON();

				$formfield.val(attachment.url);
				$('#'+ formfield +'_id').val(attachment.id);

				if ( attachment.type && attachment.type === 'image' ) {
					// image preview
					uploadStatus = '<div class="img_status"><img style="max-width: 350px; width: 100%; height: auto;" src="' + attachment.url + '" alt="'+ attachment.filename +'" title="'+ attachment.filename +'" /><p><a href="#" class="cpmb_remove_file_button" rel="' + formfield + '">'+ cpmb_l10.remove_image +'</a></p></div>';
				} else {
					// Standard generic output if it's not an image.
					uploadStatus = cpmb_l10.file +' <strong>'+ attachment.filename +'</strong>&nbsp;&nbsp;&nbsp; (<a href="'+ attachment.url +'" target="_blank" rel="external">'+ cpmb_l10.download +'</a> / <a href="#" class="cpmb_remove_file_button" rel="'+ formfield +'">'+ cpmb_l10.remove_file +'</a>)';
				}

				// add/display our output
				$formfield.siblings('.cpmb_media_status').slideDown().html(uploadStatus);
			}
		});

		// Finally, open the modal
		file_frames[formfield].open();
	})
	.on( 'click', '.cpmb_remove_file_button', function(event) {
		var $self = $(this);
		if ( $self.is( '.attach_list .cpmb_remove_file_button' ) ){
			$self.parents('li').remove();
			return false;
		}
		formfield    = $self.attr('rel');
		var $container   = $self.parents('.img_status');

		$('input#' + formfield).val('');
		$('input#' + formfield + '_id').val('');
		if ( ! $container.length ) {
			$self.parents('.cpmb_media_status').html('');
		} else {
			$container.parent().html('');
		}
		return false;
	})
	.on( 'click', '.add-row-button', function(e) {

		e.preventDefault();
		self = $(this);

		var tableselector = '#'+ self.data('selector');
		var $table = $(tableselector);
		var row = $('.empty-row', $table).clone(true);
		row.removeClass('empty-row').addClass('repeat-row');
		row.insertBefore( tableselector +' tbody>tr:last' );
		var input = $('input.cpmb_datepicker',row);
		var id = input.attr('id');
		input.attr('id', id + iterator );
		iterator++;

		// @todo Make a colorpicker field repeatable
		// row.find('.wp-color-result').remove();
		// row.find('input:text.cpmb_colorpicker').wpColorPicker();

	})
	.on( 'click', '.remove-row-button', function(e) {
		e.preventDefault();
		var $self = $(this);
		var $parent = $self.parents('.cpmb-repeat-table');
		console.log( 'number of tbodys', $parent.length );
		console.log( 'number of trs', $('tr', $parent).length );
		if ( $('tr', $parent).length > 2 )
			$self.parents('.cpmb-repeat-table tr').remove();
	})

	/**
	 * Ajax oEmbed display
	 */

	// ajax when typing
	.on( 'keyup', '.cpmb_oembed', function(event) {
		// fire our ajax function
		doCPMBajax($(this), event);
	});

	// ajax on paste
	$('.cpmb_oembed').bind( 'paste', function(e) {
		var pasteitem = $(this);
		// paste event is fired before the value is filled, so wait a bit
		setTimeout( function() {
			// fire our ajax function
			doCPMBajax(pasteitem, 'paste');
		}, 100);
	}).blur( function() {
		// when leaving the input
		setTimeout( function() {
			// if it's been 2 seconds, hide our spinner
			$('.postbox .cpmb_metabox .cpmb-spinner').hide();
		}, 2000);
	});

	// function for running our ajax
	function doCPMBajax(obj, e) {
		// get typed value
		var oembed_url = obj.val();
		
		// only proceed if the field contains more than 6 characters
		if (oembed_url.length < 6)
			return;

		// only proceed if the user has pasted, pressed a number, letter, or whitelisted characters
		if (e === 'paste' || e.which <= 90 && e.which >= 48 || e.which >= 96 && e.which <= 111 || e.which == 8 || e.which == 9 || e.which == 187 || e.which == 190) {

			// get field id
			var field_id = obj.attr('id');
			// get our inputs context for pinpointing
			var context = obj.parents('.cpmb_metabox .cpmb-row .cpmb-col');
			//******** Lineas Modificadas Start ********//
			
			if( context.find('.custom_repeatable').length)
				context = obj.parent();
				
			//******** Lineas Modificadas End ********//

			var embed_container = $('.embed_status', context);
			var oembed_width = obj.width();
			var child_el = $(':first-child', embed_container);

			oembed_width = ( embed_container.length && child_el.length )
				? child_el.width()
				: obj.width();

			// show our spinner
			$('.cpmb-spinner', context).show();
			// clear out previous results
			$('.embed_wrap', context).html('');
			// and run our ajax function
			setTimeout( function() {
				// if they haven't typed in 500 ms
				if ($('.cpmb_oembed:focus').val() != oembed_url)
					return;
				$.ajax({
					type : 'post',
					dataType : 'json',
					url : window.cpmb_l10.ajaxurl,
					data : {
						'action': 'cpmb_oembed_handler',
						'oembed_url': oembed_url,
						'oembed_width': oembed_width > 300 ? oembed_width : 300,
						'field_id': field_id,
						'object_id': window.cpmb_l10.object_id,
						'object_type': window.cpmb_l10.object_type,
						'cpmb_ajax_nonce': window.cpmb_l10.ajax_nonce
					},
					success: function(response) {
						// Make sure we have a response id
						if (typeof response.id === 'undefined')
							return;

						// hide our spinner
						$('.cpmb-spinner', context).hide();
						// and populate our results from ajax response
						$('.embed_wrap', context).html(response.result);
					}
				});

			}, 500);
		}
	}

	/**
	 * Resize oEmbed videos to fit in their respective metaboxes
	 */
	function resizeoEmbeds() {
		$('table.cpmb_metabox').each( function( index ) {
			var self = $(this);
			var parents = self.parents('.inside');
			if ( ! parents.length )
				return true; // continue

			var tWidth = parents.width();
			var newWidth = Math.round((tWidth * 0.82)*0.97) - 30;
			if ( newWidth > 639 )
				return true; // continue

			var child_el = $('.cpmb-type-oembed .embed_status', self).children().first();
			var iwidth = child_el.width();
			var iheight = child_el.height();
			var newHeight = Math.round((newWidth * iheight)/iwidth);
			child_el.width(newWidth).height(newHeight);

		});
	}

	// on pageload
	setTimeout( resizeoEmbeds, 500);
	// and on window resize
	$(window).on( 'resize', resizeoEmbeds );
	
	///
	var field, fieldLocation;
	$('.repeatable-add').click(function() {
		field = $(this).closest('.cpmb-col').find('.custom_repeatable li:last').clone(true);  
		fieldLocation = $(this).closest('.cpmb-col').find('.custom_repeatable li:last');
		$('.cpmb_media_status', field).html('');  
		$('input[type="text"], input[type="hidden"]', field).val('').attr({
			'name': function(index, name) {
				return name.replace(/(\d+)/, function(fullMatch, n) {
					return Number(n) + 1;
				});
			},
			'id': function(index, id) {
				return id.replace(/(\d+)/, function(fullMatch, n) {
					return Number(n) + 1;
				});
			}
		});
		$('.cpmb_media_status', field).val('').attr({
			'id': function(index, id) {
				return id.replace(/(\d+)/, function(fullMatch, n) {
					return Number(n) + 1;
				});
			}
		});
		field.insertAfter(fieldLocation, $(this).closest('.cpmb-col'))  
		return false;  
    });  
      
    $('.repeatable-remove').click(function(){
		var custom_repeatable = $(this).closest('ul');
		var items = custom_repeatable.find('li').length;
		if( items > 1){
			$(this).parent().remove();  
		} else {
			alert('You cannot delete the last Item');
		}
		setTimeout(function(){
			restore_fields_cpmb(custom_repeatable);
		},500);
		
        return false;  
    });  
          
    $('.custom_repeatable').sortable({  
        opacity: 0.6,  
        revert: true,  
        cursor: 'move',  
        handle: '.sort' ,
		start: function (e, ui) {
			//alert("started");
		},
		update: function (e, ui) {
			restore_fields_cpmb($(this));
		},
    });
	function restore_fields_cpmb(This){
		
		This.find('li.list-repeatable').each(function (indice,obj){
			
			$('input[type="text"]:eq(0), input[type="text"]:eq(1), input[type="hidden"]:eq(0)', obj).attr({
				'name': function(index, name) {
					return name.replace(/(\d+)/, indice);
				},
				'id': function(index, id) {
					return id.replace(/(\d+)/, indice);
				}
			});
			if($('.embed_wrap', obj).length) {
				var divId = $('.embed_wrap', obj).attr('id');
				$('.embed_wrap', obj).attr('id', divId.replace(/(\d+)/,indice));
			}
			if($('.cpmb_media_status', obj).length) {
				var media_statusId = $('.cpmb_media_status', obj).attr('id');
				$('.cpmb_media_status', obj).attr('id', media_statusId.replace(/(\d+)/,indice));
			}
			if($('.cpmb_remove_file_button', obj).length) {
				var remove_mediaId = $('.cpmb_remove_file_button', obj).attr('rel');
				$('.cpmb_remove_file_button', obj).attr('rel', remove_mediaId.replace(/(\d+)/,indice));
			}
			
		});
	}

});
